import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component{
  constructor(props) {
    super(props);
    this.state = {
      response: '',
      todoListName: '',
      todoListDescription: '',
      todoListResponse: '',
      todoItemListId: '',
      todoItemDescription: '',
      todo

    };
    this.handleChange = this.handleChange.bind(this)
    this.addToDoListHandleSubmit = this.addToDoListHandleSubmit.bind(this)
  }

  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }

  callApi = async() => {
    const response = await fetch('/api/hello');

    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  handleChange = (event) => {
    let name = event.target.name;
    let val = event.target.value;
    this.setState({[name]: val});
  }

  addToDoListHandleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/v1/addToDoList', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ 
        'name': this.state.todoListName,
        'description': this.state.todoListDescription
       }),
    });
    const body = await response.text();
    this.setState({ todoListResponse: body });
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
        <p>{this.state.response}</p>
        <form onSubmit={this.addToDoListHandleSubmit}>
          <p>
            <strong>Add a todolist {this.state.todoListName}: {this.state.todoListDescription}</strong>
          </p>
          <p>Enter todoList name:</p>
          <input
            type="text"
            name="todoListName"
            onChange={this.handleChange}
          />
          <p>Enter todoList description:</p>
          <input
            type="text"
            name="todoListDescription"
            onChange={this.handleChange}
          />
          <br></br>
          <button type="submit">Submit</button>
        </form>
        <p>{this.state.todoListResponse}</p>
      </div>
    );
  }
}

export default App;
